public class RunPitaron {    public static long[] arr = new long[5];public static int[] SelectionSort(int[] array) { for (int i = 0; i < 5; i++) {arr[i] = 0; }        int n = array.length;for(int t=0;t<n;t++){        arr[0]++; for(int v=0;v<n;v++){        arr[1]++;        for (int i = 0; i < n - 1; i++) {        arr[2]++;            for (int j = 0; j < n - i - 1; j++)             {        arr[3]++;                if (array[j] > array[j + 1]) {                    int temp = array[j];                    array[j] = array[j + 1];                    array[j + 1] = temp;                }            }        }}}        return array;    }}
